import React from 'react';
import './App.css';
import { useState, useEffect } from "react";
import ArticleList from './components/ArticleList';
import Form from './components/Form';
import { useCookies } from 'react-cookie'


interface Article {
  id: number;
  title: string;
  description: string;
}

function App() {

  const [articles, setArticles] = useState<Article[]>([]);
  const [editArticle, setEditArticle] = useState<Article | null>(null);
  const [token , setToken] = useCookies(['mytoken'])


  useEffect(() => {
    fetch('http://127.0.0.1:8000/api/articles/', {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
        'Authorization':`Token ${token}`
      },
    })
    .then(resp => resp.json())
    .then(resp => setArticles(resp))
    .catch(error => console.log(error))
  }, [])

  const editBtn = (article: Article) => {
    setEditArticle(article);
  };

  const updatedInformation = (article: Article) => {
    const new_article = articles.map(myarticle => {
      if(myarticle.id === article.id) {
        return article;
      }
      else {
        return myarticle;
      }
    })
    setArticles(new_article)
  }

  const articleForm = () => {
    setEditArticle({
      id: 0,
      title: '',
      description: '',
    });
  };

  const insertedInformation = (article: Article) => {
    const new_articles = [...articles, article]
    setArticles(new_articles)
  }

  const deleteBtn = (article: Article) => {
    const new_articles = articles.filter(myarticle => {
      if(myarticle.id === article.id) {
        return false
      }
      return true
    })
    setArticles(new_articles)
  }

  return (
    <div className="App">

      <div className='row'>
      <div className='col'>
      <h1>Django And ReactTS Blog App</h1>
      <br/>
      <br/>
      </div>

      <div className='col'>
        <button onClick={articleForm} className='btn btn-primary'>Insert Article</button>
      </div>

    </div>  

      <ArticleList articles={articles} editBtn={editBtn} deleteBtn={deleteBtn} />
      <Form article = {editArticle} updatedInformation = {updatedInformation} insertedInformation = {insertedInformation} />

    </div>
  );
}

export default App;
