import React from 'react';
import './App.css';
import { useState, useEffect } from "react";
import ArticleList from './components/ArticleList';
import Form from './components/Form';


interface Article {
  id: number;
  title: string;
  description: string;
}

function App() {

  const [articles, setArticles] = useState<Article[]>([]);
  const [editArticle, setEditArticle] = useState<Article | null>(null);


  useEffect(() => {
    fetch('http://127.0.0.1:8000/api/articles/', {
      'method':'GET',
      headers: {
        'Content-Type':'application/json',
        'Authorization':'Token 90967bf73a23b66a6f1cc9fa584a9da33ff4aaad'
      },
    })
    .then(resp => resp.json())
    .then(resp => setArticles(resp))
    .catch(error => console.log(error))
  }, [])

  const editBtn = (article: Article) => {
    setEditArticle(article);
  };

  const updatedInformation = (article: Article) => {
    const new_article = articles.map(myarticle => {
      if(myarticle.id === article.id) {
        return article;
      }
      else {
        return myarticle;
      }
    })
    setArticles(new_article)
  }

  return (
    <div className="App">
      <h1>Django And ReactTS Blog App</h1>
      <br/>
      <br/>

      <ArticleList articles={articles} editBtn={editBtn} />
      <Form article = {editArticle} updatedInformation = {updatedInformation} />

    </div>
  );
}

export default App;
